mapper.select:
select
  id, role_id, permission_id, deleted, create_by, create_time, update_by, update_time
from sys_role_permission


        and id = #{id}


        and role_id = #{roleId}


        and permission_id = #{permissionId}


        and deleted = #{deleted}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sys_role_permission


        and id = #{id}


        and role_id = #{roleId}


        and permission_id = #{permissionId}


        and deleted = #{deleted}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}
